function readInt16x100(value) {
    if (value > 32767) {
        value = value - 65536;
    }

    return value / 100.0;
}

function readUint16x100(value) {
    return value / 100.0;
}


function readUint32(MSR, LSR) {
    return (MSR << 16) + LSR;
}

function readInt32(MSR, LSR) {
    var value = (MSR << 16) + LSR;

    if (value > 2147483647) {
        value = value - 4294967296;
    }

    return value;
}

